import { useEffect, useRef } from 'react';
export function useEffectOnUpdate(callback, deps) {
    var isFirstRender = useRef(true);
    useEffect(function () {
        if (isFirstRender.current) {
            isFirstRender.current = false;
        }
        else {
            return callback();
        }
    }, deps);
}
