import { useRef, useEffect } from 'react';
export var usePreviousFrameValue = function (value) {
    var ref = useRef();
    useEffect(function () {
        var handle = requestAnimationFrame(function () {
            ref.current = value;
        });
        return function () { return cancelAnimationFrame(handle); };
    });
    return ref.current;
};
